import React, { Fragment } from "react";
import { Link, Route, useParams, useRouteMatch } from "react-router-dom";
import Comments from "../comments/Comments";
import HighlightQuote from "../quotes/HighlightedQuote";

const DUMMMY_QUOTES = [
  {id:'q1' , author:'schad Botombe', text:'Learning react is fun!!' },
  {id:'q2' , author:'fred legusta', text:'Learning react is fun!!' },
  {id:'q3' , author:'tom hendricks', text:'Learning react is fun!!' },
]


const QuoteDetail = () => {
  const match = useRouteMatch();
  console.log(match);

  const params = useParams();
  const quotesData = quotes.find((quote) => quote.id === params.quoteId);

  if (!quotesData) {
    return <p>No Quote found!!</p>;
  }

  return (
    <Fragment>
      <HighlightQuote text={quotesData.text} author={quotesData.author} />
      <Route path={match.path} exact>
        <div className="centered">
          <Link className="btn--flat" to={`${match.url}/comments`}>
            Load Comments
          </Link>
        </div>
      </Route>
      <Route path={`${match.path}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
};

export default QuoteDetail;
